import React, { Component } from "react";
import CB from "./CB.jsx";

class CBS extends Component {
  state = {
    CB: [{ id: 1, user: 1, rembut: 0, idx: 1 }]
  };

  handleDelete = passedId => passedIdx => {
    let cb = this.state.CB.filter(c => c.id !== passedId);
    this.setState({ CB: cb });
    const newcb = { id: passedId + 1, user: 1, rembut: 1, idx: passedIdx };
    cb.push(newcb);
  };

  render() {
    return (
      <div>
        {this.state.CB.map(tag => (
          <CB
            key={tag.id}
            onDelete={this.handleDelete}
            idx={tag.idx}
            user={tag.user}
            id={tag.id}
            remButton={0}
          />
        ))}
      </div>
    );
  }
}

export default CBS;
