import React, { Component } from "react";
import CB from "./CB.jsx";

class CBS extends Component {
  state = {
    CB: [{ id: 1, user: 1, rembut: 0, idx: 1 }]
  };

  handleDelete = id => {
    //console.log("delete button handled", id);
    const cb = this.state.CB.filter(c => c.id !== id);
    this.setState({ CB: cb });
    //usi ko bina button k add karde
    console.log(this.state.CB);
    this.state.CB += { id: 2, user: 1, rembut: 0, idx: 1 };
    console.log(this.state.CB);
  };

  render() {
    return (
      <div>
        {this.state.CB.map(tag => (
          <CB
            key={tag.id}
            onDelete={this.handleDelete}
            idx={tag.idx}
            user={tag.user}
            id={tag.id}
            remButton={0}
          />
        ))}
      </div>
    );
  }
}

export default CBS;
