import React, { Component } from "react";
import CB from "./CB.jsx";

class CBS extends Component {
  state = {
    CB: [{ id: 1, user: 1, remButton: 0, idx: 1 }]
  };

  handleDelete = passedId => {
    let ncb = this.state.CB.filter(c => c.id === passedId);
    let cb = this.state.CB.filter(c => c.id !== passedId);
    this.setState({ CB: cb });
    ncb[0].remButton = 1;
    cb.push(ncb[0]);
    console.log("hi", ncb[0].remButton);
    this.setState({ CB: cb });
  };

  render() {
    console.log(this.state.CB);
    return (
      <div>
        {this.state.CB.map(tag => (
          <CB
            key={tag.id}
            onDelete={this.handleDelete}
            idx={tag.idx}
            user={tag.user}
            id={tag.id}
            remButton={0}
          />
        ))}
      </div>
    );
  }
}

export default CBS;
